import { useState } from "react";

function App() {
  const [job , setJob ] = useState('')
  const [ jobs , setJobs ] = useState([])


  const handler = () => {
    setJobs(prev => [...prev, job])
    setJob('')
  }


  return (
    <div style={{padding : 32}}>
      <input
        value={job}
        onChange={e => setJob(e.target.value)}
      />
      <button
      onClick={() => {
        handler()
      }}
      >add</button>

      <ul>
        {jobs.map((job , index) => {
          <div>
          <li key={index}>{job}</li>
          </div>
        })}
      </ul>
    </div>
  );
}

export default App;
