import { useState } from "react";

function App() {
  const [job , setJob ] = useState('')
  const [ jobs , setJobs ] = useState([])


  const handleSubmit = () => {
    setJobs(prev => [...prev, job])
    console.log(jobs)
  }


  return (
    <div style={{padding : 32}}>
      <input
        value={job}
        onChange={e => setJob(e.target.value)}
      />
      <button
      onClick={handleSubmit}
      >add</button>

      <ul>
        {jobs.map(( job , index ) => {
          <li key={index}>{job}</li>

        })}
      </ul>
    </div>
  );
}

export default App;
