import { useEffect, useState } from "react"

function Content() {

    const [CountDown, setCountDown] = useState(180)

    useEffect(() =>{
        const timer = setInterval(() => {
            setCountDown(pre=>pre - 1)
        }, 1000);

        return ()=>{
            clearInterval(timer)
        }
    }, [])
    return (
        <div>
            {CountDown}
        </div>
    )
}

export default Content
