// mounted / unmounted

function Content() {
  const [countdown , setCountdown] = useState(180)

  useEffect(() => {
    setImmediate((prevState => prevState - 1))
  },[])

  return (
    <div>
      <h1>{countdown}</h1>
    </div>
  );
}

export default Content;
