import { useEffect, useState } from "react"

function Content() {

    const [CountDown, setCountDown] = useState(10)

    useEffect(() =>{
        const timer = setInterval(() => {
            setCountDown(pre=>pre - 1)
        }, 1000);

        return ()=>{
            clearInterval(timer)
        }
    }, [])
    return (
        <div>
            <h1>{CountDown}</h1>
        </div>
    )
}

export default Content
