import { useEffect, useState } from "react";
import Content from "./Content";

// mounted / unmounted

function App() {

  const [countdown , setCountdown ] = useState(10)

  useEffect(() => {
    setInterval(() => {
      setCountdown(prevState => prevState - 1)
    }, 1000);
  }, [])
  return (
    <div>
      <h1>{countdown}</h1>
    </div>
  );
}

export default App;
