import { useEffect, useState } from "react";

// mounted / unmounted

function Content() {

  const [countdown , setCountdown ] = useState(180)

  useEffect(() => {
    setInterval(() => {
      setCountdown(countdown - 1)
    }, 1000)
  }, [countdown])

  return (
    <div>
      <h1>{countdown}</h1>
    </div>
  );
}

export default Content;
